$(document).ready(function(){
	//Index login function (call)
	$(".input_button p a").click(function(){
		let inputPass = $(".password").val();
		if (inputPass === "password_confirmation"){
			alert("correct password, confirm to redirect");
			window.location.replace("project/index.html");//Redirect 
		} else {
			alert("wrong password");
		}
	})
})